import json
import base64

json_string="""
{
  "binarized_image": ""
}
"""

data = json.loads(json_string)

base64_string = data.get('binarized_image', '')

if base64_string:
    # Декодируем base64
    image_bytes = base64.b64decode(base64_string)

    # Сохраняем изображение
    with open('output.png', 'wb') as img_file:
        img_file.write(image_bytes)
    print("Изображение успешно сохранено как output.png")
else:
    print("Base64-строка не найдена в JSON.")
